let toggleLike=function(t){let e=$(" span",$($(t).parent())),n=$(e).text();event.preventDefault(),$.ajax({type:"get",url:$(t).prop("href"),success:function(t){n=t.data.deleted?+n-1:+n+1,$(e).text(n),notifications("success",t.message)},error:function(t){notifications("error",t.responseText)}})},notifications=function(t,e){new Noty({theme:"relax",text:e,type:t,layout:"topRight",timeout:1e3}).show()};